mapper.select:
SELECT * FROM user_file
WHERE user_id = #{userId, jdbcType=VARCHAR}
ORDER BY create_datetime, mapper_order ASC
LIMIT #{start}, #{pageSize}

SELECT * FROM user_file
WHERE file_id = #{fileId, jdbcType=VARCHAR}
ORDER BY create_datetime, mapper_order ASC
LIMIT #{start}, #{pageSize}

SELECT * FROM user_file
WHERE user_id = #{userId, jdbcType=VARCHAR}
ORDER BY enable, create_datetime DESC
